css:
.print-format {
    font-size: 10pt;
    padding: 0.5in;
}

.print-heading {
    text-align: center;
    padding-bottom: 5px;
}

hr {
    margin-top: 10px;
    margin-bottom: 5px;
}

table {
    font-size: 11px;
}

.print-format td, .print-format th {
    padding: 2px !important;
}

@media print {
    #footer-html {
        display: flex;
        flex-direction: column;
    }
}

