                {% if ipd_cons %}
                    {% for item in ipd_cons %}
                        {% set item_category = frappe.db.get_value('Item', item.consultation_item, 'item_group') %}
                        {% set _ = bill.append({"date": item.date, "category": item_category, "item_name": item.consultation_item, "quantity": 1, 'item_rate': item.rate, "amount": item.rate }) %}
                    {% endfor %}
                {% endif %}
            {% endif %}

            <TABLE border=1 style="width: 100%; border-collapse: collapse; border: 1px grey; margin-bottom: 10px;">
                <COLGROUP>
                    <COL WIDTH="5%">
                    <COL WIDTH="11%">
                    <COL WIDTH="25%">
                    <COL WIDTH="36%">
                    <COL WIDTH="5%">
                    <COL WIDTH="8%">
                    <COL WIDTH="15%">
                </COLGROUP>
                <THEAD style="background-color: darkgray;">
                    <TR>
                        <TH style="text-align: center;">&nbsp;</TH>
                        <TH style="text-align: center;">Date</TH>
                        <TH style="text-align: center;">Category</TH>
                        <TH style="text-align: center;">Description</TH>
                        <TH style="text-align: center;">Qty</TH>
                        <TH style="text-align: center;">Rate</TH>
                        <TH style="text-align: center;">Amount</TH>
                    </TR>
                </THEAD>

                <TBODY>
                    {% set total = [0] %}
                    {% for item in bill %}
                      {% set total = total.append(total.pop() + item.amount) %}
                        <TR>
                            <TD style="text-align: center;">
                                {{ loop.index }}
                            </TD>
                            <TD style="text-align: center;">
                                {{frappe.utils.formatdate(item.date, "dd-MMM-YYYY") }}
                            </TD>
                            <TD style="text-align: left;">
                                {{ item.category }}
                            </TD>
                            <TD style="text-align: left;">
                                {{ item.item_name }}
                            </TD>
                            <TD style="text-align: center;">
                                {{ item.quantity|int }}
                            </TD>
                            <TD style="text-align: right;">
                                {{ frappe.utils.fmt_money(item.item_rate, precision=0) }}
                            </TD>
                            <TD style="text-align: right;">
                                {{ frappe.utils.fmt_money(item.amount, precision=0) }}
                            </TD>
                        </TR>
                    {% endfor %}
                </TBODY>

                <TFOOT>
                   <TR>
                      <TD style="text-align: center;">&nbsp;</TD>
                      <TD style="text-align: left;" colspan="5">TOTAL</TD>
                      <TD style="text-align: right;">{{ frappe.utils.fmt_money(total[0], precision=0) }}</TD>
                      
                   </TR> 
                </TFOOT>
            </TABLE>
        {% endif %}
    {% else %}
        This appointment is not closed
    {% endif %}
{% endif %}"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<p class="text-center">Printed By: <b>{{ frappe.db.get_value("User", frappe.session.user, "full_name") }}</b></p>"}]
