format_data:
[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading"></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set patient_details = frappe.get_all("Patient", {"name": doc.patient}, ["*"]) %}

<table class="table table-sm" style="margin-top: 0px">
    <tr class="paragraph1">{## set dob = doc.date_of_birth ##}
        <td>
            {{doc.patient_name}},  
            {{patient_details[0].sex}}, 
            {% set now  = frappe.utils.nowdate() %}
            {% set diff = frappe.utils.date_diff(now, patient_details[0].dob) %}
            {% set year = diff//365 %}
            {% set month = (diff - (year * 365))//30 %}
            {% set day = ( (diff - (year * 365)) - (month * 30) ) %}
                
            {{ year }} Years, {{ month }} Months, {{day}} Days, 
            {{doc.insurance_coverage_plan}}, Reg #: {{doc.patient}}
        </td>
    </tr>
    <tr>
        <td class="empty_td_row"></td>
    </tr>
    <tr class="paragraph2">
        <td>
            <b>OUTPATIENT DETAILS</b>
        </td>
    </tr>
</table>


{% set encounter_details = frappe.get_all("Patient Encounter", {"appointment": doc.appointment}, ["*"]) %}
{% for encounter in  encounter_details%}
<table class="table table-sm" border=0>
    <colgroup>
       <col width="16%">
       <col width="12%">
       <col width="1%">
       <col width="8%">
       <col width="14%">
       <col width="49%"> 
    </colgroup>
    <thead class="thead1">
        <tr>
            <td class="text-right">Doctor</td>
            <td colspan=4 class="text-left">{{encounter["practitioner_name"]}}</td>
            <td class="text-left">
               Date: {{ encounter["creation"].strftime('%Y-%m-%d') }} {{ encounter["creation"].strftime('%H:%M:%S') }}
            </td>
        </tr>
    </thead>
    
    <tbody>
        <tr>
            <td colspan=2 style="text-align: right; vertical-align: middle !important;">Main Complain:</td>
            <td style="text-align: right; vertical-align: middle !important;"></td>
            <td colspan=3 style="text-align: left; vertical-align: top !important;">
                {% set complaints = frappe.db.get_all("Patient Encounter Symptom", {"parent": encounter["name"]}, ["complaint", "complaint_duration"]) %}
                {% if complaints %}
                    {% for comp in complaints %}
                        {{comp["complaint"]}} - {{comp["complaint_duration"]}},
                    {% endfor %}
                {% endif %}
            </td>
        </tr>
        <tr>
            <td colspan=2 style="text-align: right; vertical-align: middle !important;">History of Present Illness:</td>
            <td style="text-align: right; vertical-align: middle !important;"></td>
            <td colspan=3 style="text-align: left; vertical-align: top !important;">
                {% if encounter["examination_detail"] %}
                    {{encounter["examination_detail"]}}
                {% endif %}
            </td>
        </tr>
        <tr>
            <td colspan=2 style="text-align: right; vertical-align: middle !important;">Provisional Diagnosis:</td>
            <td style="text-align: right; vertical-align: middle !important;"></td>
            <td colspan=3 style="text-align: left; vertical-align: top !important;">
                {% set pre_diagnosis = frappe.get_all("Codification Table", {"parent": encounter["name"]}, ["medical_code", "code", "description", "mtuha"]) %}
                {% if pre_diagnosis %}
                    {% for diagnosis in pre_diagnosis %}
                        {%if diagnosis["description"]%} {{diagnosis["description"]}} {%endif%} {%if diagnosis["mtuha"] %} - {{diagnosis["mtuha"]}} {%endif%}, 
                    {% endfor %}
                {% endif %}
            </td>
        </tr>
        <tr>
            <td colspan=2 style="text-align: right; vertical-align: middle !important;">Lab:</td>
            <td style="text-align: right; vertical-align: middle !important;"></td>
            <td colspan=3 style="text-align: left; vertical-align: top !important;">
                {% set lrpmt_pres = frappe.get_all("Lab Prescription", {"parent": encounter["name"]}, ["lab_test_code"]) %}
                {% if lrpmt_pres %}
                    {% for lab in lrpmt_pres %}
                        {{lab["lab_test_code"]}}
                    {% endfor %}
                {% endif %}
            </td>
        </tr>
        <tr>
            <td colspan=2 style="text-align: right; vertical-align: middle !important;">Radiology:</td>
            <td style="text-align: right; vertical-align: middle !important;"></td>
            <td colspan=3 style="text-align: left; vertical-align: top !important;">
                {% set lrpmt_pres = frappe.get_all("Radiology Procedure Prescription", {"parent": encounter["name"]}, ["radiology_examination_template"]) %}
                {% if lrpmt_pres %}
                    {% for rad in lrpmt_pres %}
                        {{rad["radiology_examination_template"]}}
                    {% endfor %}
                {% endif %}
            </td>
        </tr>
        <tr>
            <td colspan=2 style="text-align: right; vertical-align: middle !important;">Procedure:</td>
            <td style="text-align: right; vertical-align: middle !important;"></td>
            <td colspan=3 style="text-align: left; vertical-align: top !important;">
                {% set lrpmt_pres = frappe.get_all("Procedure Prescription", {"parent": encounter["name"]}, ["procedure"]) %}
                {% if lrpmt_pres %}
                    {% for proc in lrpmt_pres %}
                        {{proc["procedure"]}}
                    {% endfor %}
                {% endif %}
            </td>
        </tr>
        <tr>
            <td colspan=2 style="text-align: right; vertical-align: middle !important;">Final Diagnosis:</td>
            <td style="text-align: right; vertical-align: middle !important;"></td>
            <td colspan=3 style="text-align: left; vertical-align: top !important;">
                {% set final_diagnosis = frappe.get_all("Codification Table", {"parent": encounter["name"]}, ["medical_code", "code", "description", "mtuha"]) %}
                {% if final_diagnosis %}
                    {% for diagnosis in final_diagnosis %}
                        {%if diagnosis["description"]%} {{diagnosis["description"]}} {%endif%}
                        {%if diagnosis["mtuha"]%} - {{diagnosis["mtuha"]}} {%endif%},
                    {% endfor %}
                {% endif %}
            </td>
        </tr>
        <tr>
            <td colspan=2 style="text-align: right; vertical-align: middle !important;">Medication:</td>
            <td style="text-align: right; vertical-align: middle !important;"></td>
            <td colspan=3 style="text-align: left; vertical-align: top !important;">
                {% set lrpmt_pres = frappe.get_all("Drug Prescription", {"parent": encounter["name"]}, ["drug_code", "dosage", "quantity"]) %}
                {% if lrpmt_pres %}
                    {% for drug in lrpmt_pres %}
                        {{drug["drug_code"]}} - {{drug["dosage"]}} - {{drug["quantity"]}}, 
                        {{drug["drug_code"]}} - Dosage - {{drug["dosage"]}} - Period - {{drug["period"]}} - Dosage Form - {{drug["dosage_form"]}} - {{drug["quantity"]}}, 

                    {% endfor %}
                {% endif %}
            </td>
        </tr>
        <tr>
            <td colspan=2 style="text-align: right; vertical-align: middle !important;">Therapy:</td>
            <td style="text-align: right; vertical-align: middle !important;"></td>
            <td colspan=3 style="text-align: left; vertical-align: top !important;">
                {% set lrpmt_pres = frappe.get_all("Therapy Plan Detail", {"parent": encounter["name"]}, ["therapy_type"]) %}
                {% if lrpmt_pres %}
                    {% for ther in lrpmt_pres %}
                        {{ther["therapy_type"]}}
                    {% endfor %}
                {% endif %}
            </td>
        </tr>
    </tbody>
</table>
{% endfor %}

"}]
